CREATE VIEW `v01_barang_satuan`  AS  select `t03_barang`.`id` AS `id`,`t03_barang`.`Nama` AS `Nama`,`t02_satuan`.`Nama` AS `Satuan` from (`t03_barang` join `t02_satuan` on((`t03_barang`.`satuan_id` = `t02_satuan`.`id`))) ;
CREATE VIEW `v02_terima_keluar`  AS  select `t08_penerimaan`.`Tanggal` AS `tanggal`,`t08_penerimaan`.`Keterangan` AS `ket1`,'' AS `ket2`,'' AS `ket3`,`t08_penerimaan`.`NoKwitansi` AS `ket4`,'' AS `ket5`,0 AS `nilai1`,'' AS `ket6`,0 AS `nilai2`,`t08_penerimaan`.`Jumlah` AS `terima_jumlah`,0 AS `keluar_jumlah`,`t08_penerimaan`.`Jumlah` AS `saldo` from `t08_penerimaan` union select `a`.`Tanggal` AS `tanggal`,`b`.`Nama` AS `maingroup_nama`,`c`.`Nama` AS `subgroup_nama`,`d`.`Nama` AS `supplier_nama`,`a`.`NoNota` AS `nonota`,`e`.`Nama` AS `barang_nama`,`a`.`Banyaknya` AS `banyaknya`,`e`.`Satuan` AS `barang_satuan`,`a`.`Harga` AS `harga`,0 AS `terima_jumlah`,`a`.`Jumlah` AS `keluar_jumlah`,`a`.`Jumlah` AS `Jumlah` from ((((`t06_pengeluaran` `a` left join `t04_maingroup` `b` on((`a`.`maingroup_id` = `b`.`id`))) left join `t05_subgroup` `c` on((`a`.`subgroup_id` = `c`.`id`))) left join `t01_supplier` `d` on((`a`.`supplier_id` = `d`.`id`))) left join `v01_barang_satuan` `e` on((`a`.`barang_id` = `e`.`id`))) ;